mapper.select:
SELECT lat, lng
FROM houseInfo
WHERE aptCode = #{aptCode}


SELECT *
FROM transportation
WHERE abs(lat-#{aptLat}) < 0.012 and abs(lng-#{aptLng}) < 0.012;

SELECT *
FROM housedeal
WHERE aptCode = #{aptCode}

limit 1, 10;

mapper.update:
UPDATE baseaddress SET view
= view + 1
WHERE dongCode=#{dongCode}
